(function(){
Template.body.addContent((function() {
  var view = this;
  return [ HTML.Raw("<h3>Embark - SimpleStorage Demo</h3>\n\n  "), Spacebars.include(view.lookupTemplate("set_value")), "\n\n  ", Spacebars.include(view.lookupTemplate("current_value")), HTML.Raw('\n\n  <div class="logs">\n  </div>') ];
}));
Meteor.startup(Template.body.renderToDocument);

Template.__checkName("current_value");
Template["current_value"] = new Template("Template.current_value", (function() {
  var view = this;
  return HTML.DIV(HTML.Raw('\n    <button class="get">Get Value</button>\n    <br>value is '), HTML.SPAN({
    "class": "value"
  }, Blaze.View("lookup:value", function() {
    return Spacebars.mustache(view.lookup("value"));
  })), "\n  ");
}));

Template.__checkName("set_value");
Template["set_value"] = new Template("Template.set_value", (function() {
  var view = this;
  return HTML.Raw('<div>\n    <input type="text" class="text" value="10">\n    <button class="set">Set Value</button>\n  </div>');
}));

})();
